import { Component, OnInit, ViewEncapsulation } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
  encapsulation: ViewEncapsulation.None // No se aplica encapsulación de estilos
})
export class HomeComponent implements OnInit {

  constructor(
    private route: ActivatedRoute) {
  }

  needsLogin: boolean | undefined;
  _userName: string = ''; // Nombre de usuario privado

  ngOnInit() {
    // Comprueba si se necesita iniciar sesión a través de los parámetros de la ruta
    this.needsLogin = !!this.route.snapshot.params['needsLogin'];
  }

  // Propiedad calculada para obtener el nombre de usuario
  get userName(): string {
    return this._userName;
  }

  // Método para iniciar sesión
  login(): void {
    this._userName = 'Matias'; // Establece el nombre de usuario
  }

  // Método para cerrar sesión
  logout(): void {
    this._userName = ''; // Borra el nombre de usuario
  }
}

<div class="card">
  <div class="header">
    <h1 class="title">Bienvenido de vuelta: {{userName}}</h1>
  </div>
  <div class="content">
    <!-- Comprueba si el usuario necesita iniciar sesión -->
    <div *ngIf="needsLogin" class="alert alert-warning">
      Has sido redirigido a esta página porque no tienes los permisos necesarios
      para la acción solicitada. ¡Inicia sesión con una cuenta de usuario correspondiente!
    </div>

    <!-- Botón para iniciar sesión -->
    <button (click)="login()" class="btn btn-default">Login</button>

    <!-- Botón para cerrar sesión -->
    <button (click)="logout()" class="btn btn-default">Logout</button>

    <!--
    Comentarios: Esta sección muestra pestañas de información relacionada con pasajeros.
    <tabbed-pane [activeId]="0">
        <tab title="Booked" [id]="0">
            Esta página podría mostrar a todos los pasajeros con reservas...
        </tab>
        <tab title="Checked in" [id]="1">
            Esta página podría mostrar a todos los pasajeros que han realizado el check-in...
        </tab>
        <tab title="Boarded" [id]="2">
            Esta página podría mostrar a todos los pasajeros que han abordado...
        </tab>
    </tabbed-pane>
    -->
  </div>
</div>
